import axios from "axios";
import { TestAPI } from "../api/TestAPI";
import React from "react";

const testArray = [1,2,3,4]


const Test = new TestAPI()
Test.makeToDos()


class TODO_COMP extends React.Component {

    removeToDo(event){
        event.preventDefault()

        axios.delete("http://localhost:4010/toDoItems/:ToDoId",{
        })
        .then((response) =>{
            console.log(response);
            window.location.reload()
        })
        .catch((error) =>{
            console.log(error);
        }) 

    }

    render() {
        return (
        <div>
            <ul>
            {this.props.toDos.map((i) => (
                <li>
                <input
                    onSubmit={this.removeToDo.bind(this)}
                    className="btn waves-effect waves-light"
                    type="checkbox"
                    nme="action"
                />
                <label key={i.content}>{i.content}</label>
                </li>
            ))}
            </ul>
        </div>
        );
    }
}

export default TODO_COMP;
