import React, {useState} from 'react'

function InputComp(props) {
  const [text, setText] = useState("")

  const handleChange = (e) =>{
    setText(e.target.value)
  }

  const handleSubmit = () =>{
    props.addToDos(text)
    setText = ""
  }

  return (
    <>
    <input

      value={text}
      onChange={(e)=>handleChange(e)}
      type="text" />

    <button
      onClick={handleSubmit}
    >Add To Do</button>
    </>
  )
}

export default InputComp
