import logo from './logo.svg';
import './App.css';
import {
  TODO_COMP
} from "./comps/MainToDo"
import ADD_TODO from './comps/AddToDo';
import React from 'react';

class App extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      toDos: [],
      currentToDo: {}
    }

    this.updateCurrentToDo = this.updateCurrentToDo.bind(this)
  }

  componentDidMount(){
    const url = "http://localhost:4010/toDos"
    axios.get(url)
      .then((reponse) => {
        this.setState({
          toDos: reponse.data
        })
      })
      .catch((error)=>{
        console.log(error);
      })


  }

  updateCurrent(item){
    this.setState({
      currentToDo: item
    })
  }


  render(){
    return (
      <div className="App">
      <ADD_TODO/>
      <TODO_COMP/>

      </div>
    );
  }

}

export default App;
