import React, {useState} from 'react';
import InputComp from './comps/InputComp';
import ToDosComp from './comps/ToDosComp';

function App(){

  const valid = (str)=>{
    if(!str) return false;

    for(i of str){
      if(i != " ")return true;
    }
    return false;
  }

  const fixSpaces = (str) =>{
    var newStr;
    for(i in str){
      if(str[i] == " " && str[i+1] == " ")
      newStr = str.slice(i);
      newStr += str.slice(i+2, str.length-1)
    }
  }

  const [state, setState] = useState([])

  const addToDos = (param) =>{

    if(valid(param) && !state.includes(param)){
      setState([...state, param])
    }else{
      console.log("It's already in there bro");
    }

  }

  return(
    <>
     <InputComp addToDos={addToDos}/>
     <ToDosComp toDos={state}/>
    </>

  )

}
export default App;
