import React from 'react';
import axios from 'axios';
import Cookies from 'universal-cookie';
const cookies = new Cookies();
cookies.set(localCount);

var localCount = 0


class ADD_TODO extends React.Component {




    addToDo(event){
        //event.preventDefault()

        axios.post("http://localhost:4010/toDoItems",{
            myID: Number(this.getNextID()),
            content: this.refs.content.value
        //  title: this.refs.title.value,
        })
        .then((response) =>{
            console.log(response);
        })
        .catch((error) =>{
            console.log(error);
        }) 
    }

    getNextID(){
        if(cookies.get(localCount) != 0)localCount++
        return cookies.get(localCount)


        console.log(this.props.toDos.length);
        if(this.props.toDos.length != 0){
            console.log("Here");
            if(this.props.toDos.length == 1){
                return this.props.toDos[0].myID+1;
            }else{
                return this.props.toDos[this.props.toDos.length-1].myID+1
            }
        }else{
            return 0
        }

    }

    render() {
        return ( 

        <div className="row">
            <h1 className='center'>ADE AND ALEX'S TO-DO LIST</h1>


            <form className="col s12" onSubmit={this.addToDo.bind(this)}>


                <div className="input-field col s6">
                    <input id="content" ref="content" type="text" />
                        <label htmlFor="content">
                            <button
                                className='btn waves-effect waves-light'
                                type="submit"
                                nme="action"
                            >
                                Add
                            </button>
                        </label>
                </div>

            </form>
        </div>

            );
    }
}

export default ADD_TODO;

