import React from 'react';
import axios from 'axios';



const skillsArray = ["shooting", "speed", "offense", "defense"]


class ADD_TODO extends React.Component {
    addToDo(event){
        event.preventDefault()
        axios.post("http://localhost:4001/players",{
            firstName: this.refs.toDoItem.value
        //    lastName: this.refs.lastName.value,
        })
        .then((response) =>{
            console.log(response);
        })
        .catch((error) =>{
            console.log(error);
        }) 
    }

    render() { 
        return ( 

            <div className="row">
            <h1 className='center'>ADE'S TO-DO LIST</h1>


            <form className="col s12" onSubmit={this.addToDo.bind(this)}>


                <div className="row">
                    <div className="input-field col s6">
                        <input id="firstName" ref="firstName" type="text" />
                            <label htmlFor="firstName"> <button className='btn waves-effect waves-light' type="submit" nme="action">Add</button>
                            </label>
                    </div>
                </div>

            </form>
            </div>

            );
    }
}

export default ADD_TODO;

const handleOnChangeEvent = (selection)=>{
    return (selection)
}
