import React from "react";
import { TestAPI } from "../api/TestAPI";


const testArray = [
    1,2,3,4
]


const Test = new TestAPI()
Test.makeToDos()

class TODO_COMP extends React.Component{
    constructor(props){
        super(props);
        this.state = {
          toDos: [],
          currentToDo: {}
        }

        this.updateCurrentToDo = this.updateCurrentToDo.bind(this)
      }

      componentDidMount(){
        const url = "http://localhost:4010/toDoItems"
        axios.get(url)
          .then((reponse) => {
            console.log(reponse.data);
            this.setState({
              toDos: reponse.data
            })
          })
          .catch((error)=>{
            console.log(error);
          })


      }

      updateCurrentToDo(item){
        this.setState({
          currentToDo: item
        })
      }

    render(){
        return (
              <ul>
                {Test.toDoList.map((i) => (
                  <li>
                    <input type="checkbox" />
                    <p key={i.title}>{i.title}</p>
                  </li>
                ))}
              </ul>
          );
    }


}


export default TODO_COMP;
