import React from 'react';
import axios from 'axios';




class ADD_TODO extends React.Component {



    addToDo(event){
        event.preventDefault()
        axios.post("http://localhost:4010/toDoItems",{
            myId: this.refs.toDoItem.value
        //    lastName: this.refs.lastName.value,
        })
        .then((response) =>{
            console.log(response);
        })
        .catch((error) =>{
            console.log(error);
        }) 
    }

    getNextID(){
        for (let i = 0; i < this.props.toDos.length; i++) {
            if(i==this.props.toDos.length-1){
                console.log("condit");
                console.log(this.props.toDos[i].myId+1)
            }            
        }
    }

    render() { 
        console.log(this.getNextID());
        return ( 

            <div className="row">
            <h1 className='center'>ADE AND ALEX'S TO-DO LIST</h1>


            <form className="col s12" onSubmit={this.addToDo.bind(this)}>


                <div className="row">
                    <div className="input-field col s6">
                        <input id="firstName" ref="firstName" type="text" />
                            <label htmlFor="firstName"> <button className='btn waves-effect waves-light' type="submit" nme="action">Add</button>
                            </label>
                    </div>
                </div>

            </form>
            </div>

            );
    }
}

export default ADD_TODO;

